
public class ArrayforEach
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		/*
		 * ARRAY WITH FOR EACH LOOP
		 */
		int num[]=new int[5];
		num[0]=43;
		num[1]=98;
		num[2]=22;
		num[3]=21;
			for(int n:num)//for each loop
				System.out.println(n);
	}

}
