
public class IterativeFor {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		/*
		 * For loop example
		 */
		int i;
			for(i=1;i<=90;i++)
				{
					System.out.println("Day"+i);
					System.out.println("Going to office by 8:00 am");
					System.out.println("Coming back to pg by 6:00 pm");
				}
	}

}
