
public class IterativeWhile 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		/*
		 * While Example
		 */
		int i=1;
		while(i<=90)
			{
				System.out.println("Day"+i);
				System.out.println("Going to office by 8:00 am");
				System.out.println("Coming back to pg by 6:00 pm");
				i++;
			}
    }

}


